import { useState } from "react";

const Form = () => {
  const [userInput, setUserInput] = useState({
    "current-savings": 10000,
    "yearly-contribution": 1200,
    "expected-return": 7,
    duration: 10,
  });

  const investmentHandler = (e) => {
    e.preventDefault();
    console.log("Submit");
  };

  // const onEnteredCurrentSaving = (e) => {
  //   setCurrentSaving(e.target.value);
  // };

  // const onEnteredYearlytSaving = (e) => {
  //   setYearlytSaving(e.target.value);
  // };

  // const onEnteredExpectedYear = (e) => {
  //   setExpectedYear(e.target.value);
  // };

  // const onEnteredInvestmentDuration = (e) => {
  //   setInvestmentDuration(e.target.value);
  // };

  const resetHandler = () => {
    console.log("Reset");
  };

  const inputChangeHandler = (input, value) => {
    setUserInput((prevInput) => {
      return {
        ...prevInput,
        [input]: value,
      };
      console.log(...prevInput);
    });
  };

  return (
    <form className="form" onSubmit={investmentHandler}>
      <div className="input-group">
        <p>
          <label htmlFor="current-savings">Current Savings ($)</label>
          <input type="number" id="current-savings" onChange={(e) => inputChangeHandler("current-savings", e.target.value)} />
        </p>
        <p>
          <label htmlFor="yearly-contribution">Yearly Savings ($)</label>
          <input type="number" id="yearly-contribution" onChange={(e) => inputChangeHandler("yearly-contribution", e.target.value)} />
        </p>
      </div>
      <div className="input-group">
        <p>
          <label htmlFor="expected-return">Expected Interest (%, per year)</label>
          <input type="number" id="expected-return" onChange={(e) => inputChangeHandler("expected-return", e.target.value)} />
        </p>
        <p>
          <label htmlFor="duration">Investment Duration (years)</label>
          <input type="number" id="duration" onChange={(e) => inputChangeHandler("duration", e.target.value)} />
        </p>
      </div>
      <p className="actions">
        <button type="reset" className="buttonAlt" onClick={resetHandler}>
          Reset
        </button>
        <button type="submit" className="button">
          Calculate
        </button>
      </p>
    </form>
  );
};

export default Form;
