import "./UserInputForm.css";

const UserInputForm = () => {
  const [userData, setUserData] = useState({
    "user-input": "suraj",
    "user-age": 30,
  });

  const inputHandler = (input, value) => {
    setUserData((prevData) => {
      return { ...prevData, [input]: value };
    });
  };

  const formSubmitHandler = (e) => {
    e.preventDefault();
  };

  return (
    <form onSubmit={formSubmitHandler}>
      <div className="form-body">
        <label htmlFor="user-input">Username</label>
        <input type="text" id="user-input" onChange={(e) => inputHandler("user-input", e.target.value)} />
      </div>
      <div className="form-body">
        <label htmlFor="user-age">Age (Yrears)</label>
        <input type="number" id="user-age" onChange={(e) => inputHandler("user-age", e.target.value)} />
      </div>
      <div className="form-body">
        <button type="submit">Add User</button>
      </div>
    </form>
  );
};

export default UserInputForm;
