import { useState } from "react";

import "./UserInputForm.css";

const UserInputForm = () => {
  const [userData, setUserData] = useState({
    "user-name": "",
    "user-age": "",
  });

  const inputHandler = (input, value) => {
    setUserData((prevData) => {
      return { ...prevData, [input]: value };
    });
  };

  const formSubmitHandler = (e) => {
    e.preventDefault();
    console.log(userData);
    setUserData({
      "user-name": "",
      "user-age": "",
    });
  };

  const validNumber = (e) => {
    if (e.target.value <= 0) {
      console.log("Enter Valid Number");
    } else {
      inputHandler("user-age", e.target.value);
    }
  };

  return (
    <form onSubmit={formSubmitHandler}>
      <div className="form-body">
        <label htmlFor="user-input">Username</label>
        <input type="text" id="user-input" value={userData["user-name"]} onChange={(e) => inputHandler("user-name", e.target.value)} />
      </div>
      <div className="form-body">
        <label htmlFor="user-age">Age (Yrears)</label>
        <input type="number" id="user-age" value={userData["user-age"]} onChange={(e) => validNumber(e)} />
      </div>
      <div className="form-body">
        <button type="submit">Add User</button>
      </div>
    </form>
  );
};

export default UserInputForm;
